$className = %packagePath ? value "."% + %className%
$bases = %list="ClassBase" @separator=","%
$interfaces = %list="ClassInterface" @separator=","%
$att = %list="Attribute" @separator="\n\n" @indent="\t" attStatic != "T"%
{
%if $bases != ""%
	LZR.initSuper(this);\n
%endIf%
%if $interfaces != ""%
	%REPLACE($interfaces, ",", ".call(this);\n\t")%.call(this);\n
%endIf%

%if $att != ""%
$att%elemType != "Interface" ? "\n"%
%endIf%

%if elemType != "Interface"%
	if (obj && obj.super_) {
		obj.super_.prototype.init_.call(this);
	} else {
		this.init_(obj);
	}
%endIf%
};

$d = ".prototype, " + $className + ".prototype);\n" + $className + ".prototype = LZR.clone ("
%if $interfaces != ""%
$className.prototype = LZR.clone (%REPLACE($interfaces, ",", $d)%.prototype, $className.prototype);
%endIf%
%if $bases != ""%
$className.prototype = LZR.clone (%REPLACE($bases, ",", $d)%.prototype, $className.prototype);
$className.prototype.super_ = [$bases];
%endIf%

$className.prototype.className_ = %qt%$className%qt%;
%PI="\n\n"%
$className.prototype.version_ = %qt%%classVersion%%qt%;
%if $className != "LZR"%
LZR.load(null, %qt%$className%qt%);
%endIf%

